import java.io.File import jxl.* import groovy.json.JsonSlurper
Workbook wb = Workbook.getWorkbook(new File('C:/ReadyAPI/Assignment/TestData.xls')) //Create new user sheet = wb.getSheet(0)
for(i=1;i<sheet.getRows();i++) { p=sheet.getCell(0,i).getContents() q=sheet.getCell(1,i).getContents() r=sheet.getCell(2,i).getContents() s=sheet.getCell(3,i).getContents() t=sheet.getCell(4,i).getContents() u=sheet.getCell(5,i).getContents()
log.info p log.info q log.info r log.info s log.info t log.info u
testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Username",p) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("FirstName",q) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("LastName",r) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Email",s) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Password",t) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("PhoneNumber",u)
