}
testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Update_Username",p1) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Update_Firstname",q1) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Update_Lastname",r1) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Update_Email",s1) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Update_Password",t1) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Update_PhoneNumber",u1)
